!(function() {

	// for dev
	$.ajaxSetup({
		crossDomain: true,
		xhrFields: {
			withCredentials: true
		}
	});
	var config = {
		host: 'http://' + location.host
	};
	var router = {
		login: 'login',
		signin: 'signin',
		index: '/'
	};
	
	var app = {
		config: config,
		router: router
	}

	window.app = app;
}());