!(function() {

	// for dev
	$.ajaxSetup({
		crossDomain: true,
		xhrFields: {
			withCredentials: true
		}
	});
	var config = {
		host: 'http://' + location.host
	};
	var router = {
		login: config.host + '/users/login',
		signin: config.host + '/users/signin',
		index: config.host + '/'
	};
	
	var app = {
		config: config,
		router: router
	}

	window.app = app;
}());